                className="bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 text-white px-8 py-6 text-lg"
              >
                Get Started Free
                <ArrowRight className="ml-2 h-5 w-5" />
              </Button>

              <Button
                size="lg"
                variant="outline"
                className="border-white/20 text-white hover:bg-white/10 px-8 py-6 text-lg"
              >
                <Terminal className="mr-2 h-5 w-5" />
                npm install @katalyst/core
              </Button>
            </div>

            <div className="mt-12 flex items-center justify-center gap-8 text-sm text-gray-400">
              <div className="flex items-center gap-2">
                <CheckCircle className="h-4 w-4 text-green-400" />
                <span>MIT License</span>
              </div>
              <div className="flex items-center gap-2">
                <CheckCircle className="h-4 w-4 text-green-400" />
                <span>TypeScript</span>
              </div>
              <div className="flex items-center gap-2">
                <CheckCircle className="h-4 w-4 text-green-400" />
                <span>Production Ready</span>
              </div>
            </div>
          </motion.div>
        </div>
      </section>

      {/* Floating Dock */}
      <FloatingDock
        items={[
          { title: "Home", icon: "üè†", href: "#" },
          { title: "Features", icon: "‚ú®", href: "#features" },
          { title: "Docs", icon: "üìö", href: "/docs" },
          { title: "GitHub", icon: "üêô", href: "https://github.com/katalyst" },
          { title: "Discord", icon: "üí¨", href: "#" }
        ]}
        className="fixed bottom-6 left-1/2 transform -translate-x-1/2 z-50"
      />

      {/* Demo Modal */}
      <AnimatePresence>
        {showDemo && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 z-50 bg-black/80 backdrop-blur-sm flex items-center justify-center p-4"
            onClick={() => setShowDemo(false)}
          >
            <motion.div
              initial={{ scale: 0.9, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.9, opacity: 0 }}
              className="bg-gray-900 rounded-2xl p-8 max-w-4xl w-full max-h-[80vh] overflow-auto"
              onClick={(e) => e.stopPropagation()}
            >
              <h3 className="text-2xl font-bold mb-4">Interactive Demo</h3>
              <p className="text-gray-400 mb-6">
                Experience Katalyst's capabilities with our live playground
              </p>
              <FrameworkShowcase />
              <Button
                className="mt-6"
                onClick={() => setShowDemo(false)}
              >
                Close Demo
              </Button>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      <style jsx>{`
        .text-gradient {
          background: linear-gradient(to right, #8b5cf6, #ec4899);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          background-clip: text;
        }
      `}</style>
    </div>
  );
}

