        loadTime: performance.now() - startTime,
        threadCount: threadPools.length,
        aiStatus: isAIEnabled,
        wasmReady: true
      });

      setIsLoaded(true);
    };

    initializeMetrics();
  }, [threadPools.length, isAIEnabled]);

  return (
    <div className="min-h-screen bg-black text-white overflow-hidden">
      {/* Background Effects */}
      <div className="fixed inset-0 z-0">
        <BackgroundGradientAnimation />
        <div className="absolute inset-0 bg-black/40" />
      </div>

      {/* Hero Section */}
      <section className="relative z-10 min-h-screen flex items-center justify-center px-4">
        <div className="absolute inset-0">
          <SparklesCore
            id="hero-sparkles"
            background="transparent"
            minSize={0.6}
            maxSize={1.4}
            particleDensity={100}
            particleColor="#FFFFFF"
          />
        </div>

        <div className="container mx-auto text-center relative">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
          >
            {/* Version Badge */}
            <motion.div 
              className="inline-flex items-center gap-2 px-4 py-2 bg-white/10 backdrop-blur-md rounded-full mb-8"
              whileHover={{ scale: 1.05 }}
            >
              <Badge className="bg-gradient-to-r from-purple-500 to-pink-500">
                v1.0.0
              </Badge>
              <span className="text-sm text-gray-300">Production Ready</span>
              <Sparkles className="w-4 h-4 text-yellow-400" />
            </motion.div>

            {/* Main Title */}
            <div className="mb-8">
              <TypewriterEffect words={HERO_WORDS} className="text-4xl md:text-6xl font-bold" />
            </div>

            {/* Subtitle */}
            <TextGenerateEffect 
              words="The Complete All-in-One Management System for Modern Web Companies"
              className="text-xl md:text-2xl text-gray-300 mb-12 max-w-3xl mx-auto"
            />

            {/* CTA Buttons */}
            <div className="flex flex-col sm:flex-row gap-4 justify-center mb-12">
              <Button
                size="lg"
                className="bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 text-white px-8 py-6 text-lg"
                onClick={() => setShowDemo(true)}
              >
                <Rocket className="mr-2 h-5 w-5" />
                Live Demo
              </Button>

              <Button
                size="lg"
                variant="outline"
                className="border-white/20 text-white hover:bg-white/10 px-8 py-6 text-lg"
                onClick={() => window.open('https://github.com/katalyst/core', '_blank')}
              >
                <Code2 className="mr-2 h-5 w-5" />
                View on GitHub
              </Button>
            </div>

            {/* Live Metrics */}
            <motion.div 
              className="grid grid-cols-2 md:grid-cols-4 gap-4 max-w-4xl mx-auto"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.5 }}
            >
              <GlowingStarsBackgroundCard className="p-4">
                <div className="text-2xl font-bold text-green-400">
                  {performanceMetrics.loadTime.toFixed(0)}ms
                </div>
                <div className="text-sm text-gray-400">Load Time</div>
              </GlowingStarsBackgroundCard>

              <GlowingStarsBackgroundCard className="p-4">
                <div className="text-2xl font-bold text-blue-400">
                  {performanceMetrics.threadCount}
                </div>
                <div className="text-sm text-gray-400">Thread Pools</div>
              </GlowingStarsBackgroundCard>

              <GlowingStarsBackgroundCard className="p-4">
                <div className="text-2xl font-bold text-purple-400">
                  {performanceMetrics.aiStatus ? 'Active' : 'Loading'}
                </div>
                <div className="text-sm text-gray-400">AI Status</div>
              </GlowingStarsBackgroundCard>

              <GlowingStarsBackgroundCard className="p-4">
                <div className="text-2xl font-bold text-yellow-400">
                  {performanceMetrics.wasmReady ? 'Ready' : 'Loading'}
                </div>
                <div className="text-sm text-gray-400">WASM</div>
              </GlowingStarsBackgroundCard>
            </motion.div>
          </motion.div>
        </div>
      </section>

      {/* Features Grid */}
      <section className="relative z-10 py-20 px-4">
        <div className="container mx-auto">
          <motion.div
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
            className="text-center mb-12"
          >
            <h2 className="text-4xl font-bold mb-4">
              Everything You Need, <span className="text-gradient">Out of the Box</span>
            </h2>
            <p className="text-xl text-gray-400 max-w-2xl mx-auto">
              Stop juggling multiple tools. Katalyst provides a complete solution for modern web companies.
            </p>
          </motion.div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {FEATURES.map((feature, index) => (
              <motion.div
                key={feature.title}
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.1 }}
              >
                <WobbleCard className="h-full">
                  <div className="p-6">
                    <div className={`inline-flex p-3 rounded-lg bg-gradient-to-br ${feature.gradient} mb-4`}>
                      <feature.icon className="h-6 w-6 text-white" />
                    </div>
                    <h3 className="text-xl font-semibold mb-2">{feature.title}</h3>
                    <p className="text-gray-400">{feature.description}</p>
                  </div>
                </WobbleCard>
              </motion.div>
            ))}
