        </div>
      </section>

      {/* Framework Showcase */}
      <section className="relative z-10 py-20 px-4 bg-gradient-to-b from-transparent to-purple-900/20">
        <div className="container mx-auto">
          <motion.div
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
            className="text-center mb-12"
          >
            <h2 className="text-4xl font-bold mb-4">See Katalyst in Action</h2>
            <p className="text-xl text-gray-400">Interactive demos of our core features</p>
          </motion.div>

          <FrameworkShowcase />
        </div>
