#!/bin/bash

# Katalyst WASM Build Script for Production
# Builds and optimizes WebAssembly modules for the storefront

set -e

echo "üöÄ Building Katalyst WebAssembly modules..."

# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
NC='\033[0m' # No Color

# Check for required tools
check_requirements() {
    echo "üìã Checking requirements..."

    if ! command -v rustc &> /dev/null; then
        echo -e "${RED}‚ùå Rust is not installed. Please install Rust first.${NC}"
        echo "Visit: https://rustup.rs/"
        exit 1
    fi

    if ! command -v wasm-pack &> /dev/null; then
        echo -e "${YELLOW}‚ö†Ô∏è  wasm-pack not found. Installing...${NC}"
        curl https://rustwasm.github.io/wasm-pack/installer/init.sh -sSf | sh
    fi

    if ! command -v wasm-opt &> /dev/null; then
        echo -e "${YELLOW}‚ö†Ô∏è  wasm-opt not found. Installing binaryen...${NC}"
        npm install -g binaryen
    fi

    echo -e "${GREEN}‚úÖ All requirements met!${NC}"
}

# Build WASM module
build_wasm() {
    echo "üî® Building WASM module..."

    cd src/wasm

    # Clean previous builds
    rm -rf pkg target

    # Build with wasm-pack
    wasm-pack build \
        --target web \
        --out-dir pkg \
        --release \
        --no-typescript

    echo -e "${GREEN}‚úÖ WASM module built successfully!${NC}"
}

# Optimize WASM binary
optimize_wasm() {
    echo "‚ö° Optimizing WASM binary..."

    # Optimize with wasm-opt
    wasm-opt \
        -O3 \
        --enable-simd \
        --enable-threads \
        --enable-bulk-memory \
        pkg/*_bg.wasm \
        -o pkg/katalyst_compute_optimized.wasm

    # Get file sizes
    ORIGINAL_SIZE=$(stat -f%z pkg/*_bg.wasm 2>/dev/null || stat -c%s pkg/*_bg.wasm 2>/dev/null)
    OPTIMIZED_SIZE=$(stat -f%z pkg/katalyst_compute_optimized.wasm 2>/dev/null || stat -c%s pkg/katalyst_compute_optimized.wasm 2>/dev/null)

    echo -e "${GREEN}‚úÖ Optimization complete!${NC}"
    echo "üìä Original size: $((ORIGINAL_SIZE / 1024))KB"
    echo "üìä Optimized size: $((OPTIMIZED_SIZE / 1024))KB"
    echo "üìä Size reduction: $(((ORIGINAL_SIZE - OPTIMIZED_SIZE) * 100 / ORIGINAL_SIZE))%"
}

# Generate TypeScript bindings
generate_bindings() {
    echo "üìù Generating TypeScript bindings..."

    cat > pkg/katalyst-compute.d.ts << 'EOF'
// Katalyst WebAssembly Compute Module TypeScript Definitions

export class KatalystCompute {
    constructor(threadCount: number, enableSimd: boolean);

    // Matrix operations
    matrix_multiply_async(a: Float32Array, b: Float32Array, size: number): Promise<Float32Array>;

    // Fast Fourier Transform
    fft_radix2(real: Float32Array, imag: Float32Array, inverse: boolean): Promise<void>;

    // Machine Learning
    k_means_clustering(
        data: Float32Array,
        dimensions: number,
        k: number,
        maxIterations: number
    ): Promise<Uint32Array>;

    // Utility functions
    fibonacci_fast(n: bigint): Promise<bigint>;
    get_performance_stats(): any;
    get_memory_usage(): number;
    run_benchmark_suite(): Promise<any>;
}

export function get_wasm_capabilities(): any;
export function allocate_buffer(size: number): number;
export function deallocate_buffer(ptr: number, size: number): void;
export function copy_memory(src: number, dst: number, size: number): void;
export function zero_memory(ptr: number, size: number): void;

// Initialize WASM module
export function init(): Promise<void>;
EOF

    echo -e "${GREEN}‚úÖ TypeScript bindings generated!${NC}"
}

# Create wrapper module
create_wrapper() {
    echo "üéÅ Creating JavaScript wrapper..."

    cat > pkg/index.js << 'EOF'
// Katalyst WASM Wrapper
import init, * as wasm from './katalyst_compute_bg.js';

let initialized = false;
let initPromise = null;

export async function initializeWasm() {
    if (initialized) return;
    if (initPromise) return initPromise;

    initPromise = init().then(() => {
        initialized = true;
        console.log('üöÄ Katalyst WASM module initialized');
    });

    return initPromise;
}

export class KatalystCompute {
    constructor(threadCount = 4, enableSimd = true) {
        if (!initialized) {
            throw new Error('WASM not initialized. Call initializeWasm() first.');
        }
        this.instance = new wasm.KatalystCompute(threadCount, enableSimd);
    }

    async matrixMultiply(a, b, size) {
        return this.instance.matrix_multiply_async(a, b, size);
    }

    async fft(real, imag, inverse = false) {
        return this.instance.fft_radix2(real, imag, inverse);
    }

    async kMeans(data, dimensions, k, maxIterations = 100) {
        return this.instance.k_means_clustering(data, dimensions, k, maxIterations);
    }

    async fibonacci(n) {
        return this.instance.fibonacci_fast(BigInt(n));
    }

    getPerformanceStats() {
        return this.instance.get_performance_stats();
    }

    getMemoryUsage() {
        return this.instance.get_memory_usage();
    }

    async runBenchmark() {
        return this.instance.run_benchmark_suite();
    }
}

export const getCapabilities = wasm.get_wasm_capabilities;
export const allocateBuffer = wasm.allocate_buffer;
export const deallocateBuffer = wasm.deallocate_buffer;
export const copyMemory = wasm.copy_memory;
export const zeroMemory = wasm.zero_memory;
EOF

    echo -e "${GREEN}‚úÖ JavaScript wrapper created!${NC}"
}

# Copy to public directory
deploy_wasm() {
    echo "üì¶ Deploying WASM module..."

    # Create directories if they don't exist
    mkdir -p ../../public/wasm
    mkdir -p ../../dist/wasm

    # Copy files
    cp pkg/katalyst_compute_optimized.wasm ../../public/wasm/
    cp pkg/*.js ../../public/wasm/
    cp pkg/*.d.ts ../../public/wasm/

    # Also copy to dist for production
    cp -r pkg/* ../../dist/wasm/ 2>/dev/null || true

    echo -e "${GREEN}‚úÖ WASM module deployed!${NC}"
}

# Main build process
main() {
    echo "‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê"
    echo "   Katalyst WASM Build System v1.0.0   "
    echo "‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê"
    echo ""

    check_requirements
    build_wasm
    optimize_wasm
    generate_bindings
    create_wrapper
    deploy_wasm

    echo ""
    echo "‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê"
    echo -e "${GREEN}‚ú® Build completed successfully!${NC}"
    echo "‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê"
    echo ""
    echo "üìç WASM module location: public/wasm/"
    echo "üìö Import in your app:"
    echo "   import { KatalystCompute } from '/wasm/index.js'"
    echo ""
}

# Run main function
main "$@"

