     1‚Üí/**
     2‚Üí * Vercel Edge Function for Katalyst Admin
     3‚Üí * 
     4‚Üí * This function serves the admin interface as a WASM module
     5‚Üí * with advanced runtime monitoring and control capabilities.
     6‚Üí */
     7‚Üí
     8‚Üíimport { NextRequest, NextResponse } from 'next/server';
     9‚Üíimport { VercelWasmAdapter, type VercelEdgeConfig } from '../packages/runtime-utils/vercel-adapter.ts';
    10‚Üí
    11‚Üí// Configuration for admin edge function
    12‚Üíconst edgeConfig: VercelEdgeConfig = {
    13‚Üí  wasmModules: ['katalyst-admin', 'katalyst-rust-core', 'katalyst-elixir-runtime'],
    14‚Üí  cacheStrategy: 'minimal', // Admin needs fresh data
    15‚Üí  timeout: 15, // Longer timeout for admin operations
    16‚Üí  memory: 256, // More memory for admin operations
    17‚Üí  regions: ['iad1'], // Single region for admin (US East)
    18‚Üí};
    19‚Üí
    20‚Üílet wasmAdapter: VercelWasmAdapter | null = null;
    21‚Üí
    22‚Üíasync function ensureInitialized(): Promise<VercelWasmAdapter> {
    23‚Üí  if (!wasmAdapter) {
    24‚Üí    wasmAdapter = new VercelWasmAdapter(edgeConfig);
    25‚Üí    await wasmAdapter.initialize();
    26‚Üí  }
    27‚Üí  return wasmAdapter;
    28‚Üí}
    29‚Üí
    30‚Üíexport const config = {
    31‚Üí  runtime: 'edge',
    32‚Üí  regions: edgeConfig.regions,
    33‚Üí};
    34‚Üí
    35‚Üíexport default async function handler(request: NextRequest) {
    36‚Üí  const startTime = Date.now();
    37‚Üí  
    38‚Üí  try {
    39‚Üí    // Basic authentication check (in production, use proper auth)
    40‚Üí    const authHeader = request.headers.get('authorization');
    41‚Üí    if (!authHeader || !isValidAuth(authHeader)) {
    42‚Üí      return new NextResponse('Unauthorized', { 
    43‚Üí        status: 401,
    44‚Üí        headers: {
    45‚Üí          'WWW-Authenticate': 'Basic realm="Katalyst Admin"'
    46‚Üí        }
    47‚Üí      });
    48‚Üí    }
    49‚Üí
    50‚Üí    const adapter = await ensureInitialized();
    51‚Üí    const url = new URL(request.url);
    52‚Üí    const pathname = url.pathname;
    53‚Üí    const method = request.method;
    54‚Üí
    55‚Üí    // Admin API routes
    56‚Üí    if (pathname.startsWith('/api/admin/')) {
    57‚Üí      return await handleAdminAPI(adapter, request, pathname, { startTime });
    58‚Üí    }
    59‚Üí    
    60‚Üí    // Admin dashboard
    61‚Üí    if (pathname.startsWith('/admin') || pathname === '/') {
    62‚Üí      return await handleAdminDashboard(adapter, request, { startTime });
    63‚Üí    }
    64‚Üí    
    65‚Üí    // Runtime metrics endpoint
    66‚Üí    if (pathname === '/metrics') {
    67‚Üí      return await handleMetrics(adapter, request, { startTime });
    68‚Üí    }
    69‚Üí    
    70‚Üí    // WASM module management
    71‚Üí    if (pathname.startsWith('/modules')) {
    72‚Üí      return await handleModuleManagement(adapter, request, { startTime });
    73‚Üí    }
    74‚Üí
    75‚Üí    return new NextResponse('Not Found', { status: 404 });
    76‚Üí    
    77‚Üí  } catch (error) {
    78‚Üí    console.error('Admin edge function error:', error);
    79‚Üí    
    80‚Üí    return NextResponse.json(
    81‚Üí      { 
    82‚Üí        error: 'Admin Internal Server Error',
    83‚Üí        message: error instanceof Error ? error.message : 'Unknown error',
    84‚Üí        timestamp: new Date().toISOString()
    85‚Üí      },
    86‚Üí      { status: 500 }
    87‚Üí    );
    88‚Üí  }
    89‚Üí}
    90‚Üí
    91‚Üíasync function handleAdminAPI(
    92‚Üí  adapter: VercelWasmAdapter,
    93‚Üí  request: NextRequest,
    94‚Üí  pathname: string,
    95‚Üí  context: { startTime: number }
    96‚Üí): Promise<NextResponse> {
    97‚Üí  const url = new URL(request.url);
    98‚Üí  const apiPath = pathname.replace('/api/admin/', '');
    99‚Üí  
   100‚Üí  switch (apiPath) {
   101‚Üí    case 'stats':
   102‚Üí      return await getSystemStats(adapter, context);
   103‚Üí    
   104‚Üí    case 'modules':
   105‚Üí      if (request.method === 'GET') {
   106‚Üí        return await listWasmModules(adapter, context);
   107‚Üí      } else if (request.method === 'POST') {
   108‚Üí        return await loadWasmModule(adapter, request, context);
   109‚Üí      }
   110‚Üí      break;
   111‚Üí      
   112‚Üí    case 'health':
   113‚Üí      return await getHealthCheck(adapter, context);
