/**
 * Vercel Edge Function for Katalyst Admin
 * 
 * This function serves the admin interface as a WASM module
 * with advanced runtime monitoring and control capabilities.
 */

import { NextRequest, NextResponse } from 'next/server';
import { VercelWasmAdapter, type VercelEdgeConfig } from '../packages/runtime-utils/vercel-adapter.ts';

// Configuration for admin edge function
const edgeConfig: VercelEdgeConfig = {
  wasmModules: ['katalyst-admin', 'katalyst-rust-core', 'katalyst-elixir-runtime'],
  cacheStrategy: 'minimal', // Admin needs fresh data
  timeout: 15, // Longer timeout for admin operations
  memory: 256, // More memory for admin operations
  regions: ['iad1'], // Single region for admin (US East)
};

let wasmAdapter: VercelWasmAdapter | null = null;

async function ensureInitialized(): Promise<VercelWasmAdapter> {
  if (!wasmAdapter) {
    wasmAdapter = new VercelWasmAdapter(edgeConfig);
    await wasmAdapter.initialize();
  }
  return wasmAdapter;
}

export const config = {
  runtime: 'edge',
  regions: edgeConfig.regions,
};

export default async function handler(request: NextRequest) {
  const startTime = Date.now();

  try {
    // Basic authentication check (in production, use proper auth)
    const authHeader = request.headers.get('authorization');
    if (!authHeader || !isValidAuth(authHeader)) {
      return new NextResponse('Unauthorized', { 
        status: 401,
        headers: {
          'WWW-Authenticate': 'Basic realm="Katalyst Admin"'
        }
      });
    }

    const adapter = await ensureInitialized();
    const url = new URL(request.url);
    const pathname = url.pathname;
    const method = request.method;

    // Admin API routes
    if (pathname.startsWith('/api/admin/')) {
      return await handleAdminAPI(adapter, request, pathname, { startTime });
    }

    // Admin dashboard
    if (pathname.startsWith('/admin') || pathname === '/') {
      return await handleAdminDashboard(adapter, request, { startTime });
    }

    // Runtime metrics endpoint
    if (pathname === '/metrics') {
      return await handleMetrics(adapter, request, { startTime });
    }

    // WASM module management
    if (pathname.startsWith('/modules')) {
      return await handleModuleManagement(adapter, request, { startTime });
    }

    return new NextResponse('Not Found', { status: 404 });

  } catch (error) {
    console.error('Admin edge function error:', error);

    return NextResponse.json(
      { 
        error: 'Admin Internal Server Error',
        message: error instanceof Error ? error.message : 'Unknown error',
        timestamp: new Date().toISOString()
      },
      { status: 500 }
    );
  }
}

async function handleAdminAPI(
  adapter: VercelWasmAdapter,
  request: NextRequest,
  pathname: string,
  context: { startTime: number }
): Promise<NextResponse> {
  const url = new URL(request.url);
  const apiPath = pathname.replace('/api/admin/', '');

  switch (apiPath) {
    case 'stats':
      return await getSystemStats(adapter, context);

    case 'modules':
      if (request.method === 'GET') {
        return await listWasmModules(adapter, context);
      } else if (request.method === 'POST') {
        return await loadWasmModule(adapter, request, context);
      }
      break;

    case 'health':
      return await getHealthCheck(adapter, context);

    case 'cache':
      if (request.method === 'DELETE') {
        return await clearCache(adapter, context);
      }
      break;

    default:
      // Delegate to WASM admin module
      const edgeHandler = adapter.createEdgeHandler('katalyst-admin');
      return await edgeHandler(request, context);
  }

  return new NextResponse('API endpoint not found', { status: 404 });
}

async function handleAdminDashboard(
  adapter: VercelWasmAdapter,
  request: NextRequest,
  context: { startTime: number }
): Promise<NextResponse> {
  const props = {
    title: 'Katalyst Admin Dashboard',
    modules: edgeConfig.wasmModules,
    timestamp: new Date().toISOString(),
    buildInfo: {
      version: '0.1.0',
      commit: 'latest',
      buildTime: new Date().toISOString(),
    },
    ...context
  };

  try {
    const html = await adapter.renderReactApp('katalyst-admin', props);

    return new NextResponse(html, {
      status: 200,
      headers: {
        'Content-Type': 'text/html',
        'Cache-Control': 'no-cache, no-store, must-revalidate',
        'X-Katalyst-Admin': 'true',
        'X-Katalyst-Duration': `${Date.now() - context.startTime}ms`,
      }
    });

  } catch (error) {
    console.error('Admin dashboard rendering error:', error);

    // Admin fallback with more detailed error info
    const fallbackHtml = `
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Katalyst Admin - Error</title>
    <style>
        body { font-family: 'Monaco', monospace; background: #000; color: #0f0; padding: 2rem; }
        .terminal { background: #000; border: 1px solid #0f0; padding: 1rem; border-radius: 4px; }
        .error { color: #f00; }
        .info { color: #0ff; }
        .timestamp { color: #ff0; }
    </style>
</head>
<body>
    <div class="terminal">
        <div class="info">KATALYST ADMIN SYSTEM</div>
        <div class="timestamp">Time: ${new Date().toISOString()}</div>
        <br>
        <div class="error">ERROR: Admin dashboard unavailable</div>
        <div class="error">Details: ${error instanceof Error ? error.message : 'Unknown error'}</div>
        <br>
        <div class="info">WASM Modules: ${edgeConfig.wasmModules.join(', ')}</div>
        <div class="info">Memory: ${edgeConfig.memory}MB</div>
        <div class="info">Timeout: ${edgeConfig.timeout}s</div>
        <br>
        <div class="info">Attempting recovery...</div>
    </div>
    <script>
        setTimeout(() => {
            window.location.reload();
        }, 5000);
    </script>
</body>
</html>`;

    return new NextResponse(fallbackHtml, {
      status: 503,
      headers: {
        'Content-Type': 'text/html',
        'Retry-After': '5',
      }
    });
  }
}

async function getSystemStats(
  adapter: VercelWasmAdapter,
  context: { startTime: number }
): Promise<NextResponse> {
  const stats = {
    timestamp: new Date().toISOString(),
    uptime: process.uptime ? process.uptime() : 0,
    memory: {
      limit: `${edgeConfig.memory}MB`,
      // In edge runtime, memory info is limited
      estimated: 'N/A',
    },
    modules: edgeConfig.wasmModules.map(name => ({
      name,
      status: 'loaded',
      size: 'N/A', // Would need actual WASM file size
    })),
    performance: {
      requestDuration: Date.now() - context.startTime,
      cacheStrategy: edgeConfig.cacheStrategy,
    },
    environment: {
      runtime: 'edge',
      regions: edgeConfig.regions,
      timeout: edgeConfig.timeout,
    }
  };

  return NextResponse.json(stats, {
    headers: {
      'Cache-Control': 'no-cache',
      'X-Katalyst-Admin-API': 'stats',
    }
  });
}

async function listWasmModules(
  adapter: VercelWasmAdapter,
  context: { startTime: number }
): Promise<NextResponse> {
  const modules = edgeConfig.wasmModules.map(name => ({
    name,
    status: 'loaded',
    type: name.includes('rust') ? 'rust' : name.includes('elixir') ? 'elixir' : 'typescript',
    loadTime: 'N/A',
    lastUsed: new Date().toISOString(),
  }));

  return NextResponse.json({
    modules,
    count: modules.length,
    timestamp: new Date().toISOString(),
  });
}

async function loadWasmModule(
  adapter: VercelWasmAdapter,
  request: NextRequest,
  context: { startTime: number }
): Promise<NextResponse> {
  const body = await request.json();
  const { moduleName, moduleData } = body;

  if (!moduleName) {
    return NextResponse.json(
      { error: 'Module name is required' },
      { status: 400 }
    );
  }

  // In a real implementation, this would load a new WASM module
  return NextResponse.json({
    message: `Module ${moduleName} loaded successfully`,
    timestamp: new Date().toISOString(),
    duration: Date.now() - context.startTime,
  });
}

async function getHealthCheck(
  adapter: VercelWasmAdapter,
  context: { startTime: number }
): Promise<NextResponse> {
  const health = {
    status: 'healthy',
    timestamp: new Date().toISOString(),
    checks: {
      wasmAdapter: adapter ? 'initialized' : 'not_initialized',
      modules: edgeConfig.wasmModules.reduce((acc, name) => {
        acc[name] = 'loaded';
        return acc;
      }, {} as Record<string, string>),
      memory: 'ok',
      performance: Date.now() - context.startTime < 1000 ? 'good' : 'slow',
    }
  };

  return NextResponse.json(health, {
    headers: {
      'Cache-Control': 'no-cache',
    }
  });
}

async function clearCache(
  adapter: VercelWasmAdapter,
  context: { startTime: number }
): Promise<NextResponse> {
  // In a real implementation, this would clear various caches
  return NextResponse.json({
    message: 'Cache cleared successfully',
    timestamp: new Date().toISOString(),
    duration: Date.now() - context.startTime,
  });
}

async function handleMetrics(
  adapter: VercelWasmAdapter,
  request: NextRequest,
  context: { startTime: number }
): Promise<NextResponse> {
  const metrics = `# HELP katalyst_requests_total Total number of requests
# TYPE katalyst_requests_total counter
katalyst_requests_total{module="admin"} 1

# HELP katalyst_request_duration_ms Request duration in milliseconds
# TYPE katalyst_request_duration_ms histogram
katalyst_request_duration_ms{module="admin"} ${Date.now() - context.startTime}

# HELP katalyst_modules_loaded Number of loaded WASM modules
# TYPE katalyst_modules_loaded gauge
katalyst_modules_loaded ${edgeConfig.wasmModules.length}

# HELP katalyst_uptime_seconds System uptime in seconds
# TYPE katalyst_uptime_seconds counter
katalyst_uptime_seconds ${process.uptime ? process.uptime() : 0}
`;

  return new NextResponse(metrics, {
    headers: {
      'Content-Type': 'text/plain',
      'Cache-Control': 'no-cache',
    }
  });
}

async function handleModuleManagement(
  adapter: VercelWasmAdapter,
  request: NextRequest,
  context: { startTime: number }
): Promise<NextResponse> {
  const url = new URL(request.url);
  const moduleName = url.pathname.split('/modules/')[1];

  if (!moduleName) {
    return await listWasmModules(adapter, context);
  }

  if (request.method === 'GET') {
    // Get module info
    return NextResponse.json({
      name: moduleName,
      status: edgeConfig.wasmModules.includes(moduleName) ? 'loaded' : 'not_found',
      timestamp: new Date().toISOString(),
    });
  }

  return new NextResponse('Method not allowed', { status: 405 });
}

function isValidAuth(authHeader: string): boolean {
  // Simple auth check - in production, use proper authentication
  const credentials = authHeader.replace('Basic ', '');
  const decoded = atob(credentials);
  const [username, password] = decoded.split(':');

  // In production, check against secure credentials
  return username === 'admin' && password === 'katalyst-admin-2024';
}

