# Function to print build summary
print_summary() {
    echo -e "\n${GREEN}üéâ Build Summary${NC}"
    echo -e "${BLUE}==================${NC}"

    if [ -d "$PUBLIC_DIR" ]; then
        local total_size=0
        for wasm_file in "$PUBLIC_DIR"/*.wasm; do
            if [ -f "$wasm_file" ]; then
                local size=$(wc -c < "$wasm_file")
                local size_kb=$((size / 1024))
                echo -e "${GREEN}üì¶ $(basename "$wasm_file"): ${size_kb}KB${NC}"
                total_size=$((total_size + size))
            fi
        done

        local total_kb=$((total_size / 1024))
        echo -e "${BLUE}üìä Total WASM size: ${total_kb}KB${NC}"
    fi

    echo -e "\n${GREEN}‚úÖ All WASM modules built successfully!${NC}"
    echo -e "${BLUE}üìÅ Output directory: $PUBLIC_DIR${NC}"
    echo -e "${BLUE}üìÑ TypeScript bindings: $PUBLIC_DIR/katalyst-wasm.d.ts${NC}"
    echo -e "${BLUE}üìÑ Manifest: $PUBLIC_DIR/manifest.json${NC}"
}

# Main build process
main() {
    echo -e "${BLUE}üöÄ Starting Katalyst WASM build process...${NC}"

    # Pre-flight checks
    check_wasm_pack

    # Clean previous builds
    echo -e "\n${YELLOW}üßπ Cleaning previous builds...${NC}"
    rm -rf "$BUILD_DIR"
    rm -rf "$PUBLIC_DIR"
    mkdir -p "$BUILD_DIR"
    mkdir -p "$PUBLIC_DIR"

    # Build each module
    build_module "rust" "$(pwd)/rust" "simd,threads"
    build_module "elixir" "$(pwd)/elixir"
    build_module "typescript" "$(pwd)/typescript"

    # Post-processing
    [ "$OPTIMIZE" = "true" ] && optimize_wasm
    generate_bindings
    create_manifest

    # Summary
    print_summary
}

# Run main function
main "$@"

