{
  "$schema": "https://openapi.vercel.sh/vercel.json",
  "name": "katalyst-multi-runtime",
  "description": "Katalyst WebAssembly Multi-Runtime Framework",
  "framework": null,
  "buildCommand": "npm run build:wasm && turbo run build --filter=@katalyst/* --cache-dir=.turbo",
  "outputDirectory": "./dist",
  "installCommand": "deno run --allow-all scripts/unified-runner.ts --install",
  "devCommand": "turbo run dev --parallel",
  "ignoreCommand": "npx turbo-ignore",
  "regions": ["iad1", "sfo1", "cdg1", "hnd1", "lhr1", "sin1"],
  "public": false,
  "github": {
    "enabled": true,
    "autoAlias": true,
    "silent": false,
    "autoJobCancelation": true
  },
  "functions": {
    "vercel-functions/katalyst-app.ts": {
      "runtime": "edge",
      "memory": 128,
      "maxDuration": 10,
      "regions": ["iad1", "sfo1", "lhr1"],
      "includeFiles": "wasm-modules/**/*.wasm"
    },
    "vercel-functions/katalyst-admin.ts": {
      "runtime": "edge", 
      "memory": 256,
      "maxDuration": 15,
      "regions": ["iad1"],
      "includeFiles": "wasm-modules/**/*.wasm"
    },
    "api/**/*.ts": {
      "runtime": "edge",
      "maxDuration": 30,
      "memory": 1024,
      "regions": ["iad1", "sfo1"]
    },
    "api/ai/**/*.ts": {
      "runtime": "edge",
      "maxDuration": 60,
      "memory": 3008,
      "includeFiles": "shared/src/ai/**"
    },
    "api/webxr/**/*.ts": {
      "runtime": "edge",
      "maxDuration": 300,
      "memory": 3008,
      "includeFiles": "shared/src/webxr/**"
    }
  },
  "routes": [
    {
      "src": "/admin(.*)",
      "dest": "/vercel-functions/katalyst-admin.ts"
    },
    {
      "src": "/api/admin(.*)",
      "dest": "/vercel-functions/katalyst-admin.ts"
    },
    {
      "src": "/metrics",
      "dest": "/vercel-functions/katalyst-admin.ts"
    },
    {
      "src": "/modules(.*)",
      "dest": "/vercel-functions/katalyst-admin.ts"
    },
    {
      "src": "/app(.*)",
      "dest": "/vercel-functions/katalyst-app.ts"
    },
    {
      "src": "/health",
      "dest": "/vercel-functions/katalyst-app.ts"
    }
  ],
  "rewrites": [
    {
      "source": "/api/v1/:path*",
      "destination": "/api/:path*"
    },
    {
      "source": "/webxr/:path*",
      "destination": "/api/webxr/:path*"
    },
    {
      "source": "/mobile/:path*",
      "destination": "/api/mobile/:path*"
    },
    {
      "source": "/wasm/(.*)",
      "destination": "/static/wasm/$1"
    }
  ],
  "headers": [
    {
      "source": "/(.*)",
      "headers": [
        {
          "key": "X-Content-Type-Options",
          "value": "nosniff"
        },
        {
          "key": "X-Frame-Options",
          "value": "DENY"
        },
        {
          "key": "X-XSS-Protection",
          "value": "1; mode=block"
        },
        {
          "key": "Referrer-Policy",
          "value": "strict-origin-when-cross-origin"
        }
      ]
    },
    {
      "source": "/api/:path*",
      "headers": [
        {
          "key": "Cache-Control",
          "value": "no-store, max-age=0"
        }
      ]
    },
    {
      "source": "/static/:path*",
      "headers": [
        {
          "key": "Cache-Control",
          "value": "public, max-age=31536000, immutable"
        }
      ]
    },
    {
      "source": "/admin/(.*)",
      "headers": [
        {
          "key": "Cache-Control",
          "value": "no-cache, no-store, must-revalidate"
        },
        {
          "key": "X-Katalyst-Admin",
          "value": "true"
        }
      ]
    },
    {
      "source": "/api/(.*)",
      "headers": [
        {
          "key": "X-Katalyst-Runtime",
          "value": "multi-wasm"
        },
        {
          "key": "X-Katalyst-Version",
          "value": "1.0.0"
        },
        {
          "key": "Access-Control-Allow-Origin",
          "value": "*"
        },
        {
          "key": "Access-Control-Allow-Methods", 
          "value": "GET, POST, PUT, DELETE, OPTIONS"
        },
        {
          "key": "Access-Control-Allow-Headers",
          "value": "Content-Type, Authorization, X-Requested-With"
        }
      ]
    }
  ],
  "env": {
    "TURBO_REMOTE_CACHE_SIGNATURE_KEY": "@turbo-remote-cache-key",
    "TURBO_TEAM": "@turbo-team",
    "TURBO_TOKEN": "@turbo-token",
    "NODE_ENV": "production",
    "KATALYST_RUNTIME": "vercel-edge",
    "WASM_OPTIMIZATION": "balanced"
  },
  "build": {
    "env": {
      "NEXT_TELEMETRY_DISABLED": "1",
      "TURBO_TELEMETRY_DISABLED": "1",
      "NODE_ENV": "production",
      "ENABLE_EXPERIMENTAL_COREPACK": "1",
      "RUST_TARGET": "wasm32-unknown-unknown",
      "DENO_RUNTIME": "true"
    }
  },
  "crons": [
    {
      "path": "/api/cron/cache-warmup",
      "schedule": "0 */6 * * *"
    },
    {
      "path": "/api/cron/analytics-digest",
      "schedule": "0 0 * * *"
    }
  ]
}

